import React from "react";

class Weather extends React.Component {
  render() {
    return(
      <div>
        <div className="location">
          { this.props.city && <p>Location: { this.props.city }, { this.props.country }</p> }
        </div>
        { this.props.temperature && <p>Current Temperature: { this.props.temperature }&deg;C</p>}
        { this.props.temp_min && <p>Daily Low: { this.props.temp_min }&deg;C</p>}
        { this.props.temp_max && <p>Daily High: { this.props.temp_max }&deg;C</p>}
        { this.props.description && <p>Current Condition: { this.props.description } <img id="wicon" src={this.props.iconurl} alt={this.props.iconcode}></img></p>}
        { this.props.wind_deg  && this.props.wind_speed  && <p>Wind: {this.props.wind_speed}m/s at {this.props.wind_deg}&deg;</p>}
        { this.props.humidity && <p>Humidity: { this.props.humidity }&#37;</p>}
        { this.props.error && <p>{ this.props.error }</p>}
      </div>
    );
  }
};

export default Weather;
