import React from "react";

import Titles from "./components/Titles";
import Form from "./components/Form";
import Weather from "./components/Weather";

const apiKey = "d971c616163c7ae140572ff2cc01b94e";

let iconcode = a.weather[0].icon;
let iconurl = "http://openweathermap.org/img/w/" + iconcode + ".png";
 $('#wicon').attr('src', iconurl);

class App extends React.Component{
  state = {
    temperature: undefined,
    city: undefined,
    country: undefined,
    description: undefined,
    humidity: undefined,
    error: undefined
  }
  getWeather = async (e) => {
    e.preventDefault();
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    const apiCall = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${apiKey}&units=metric`);
    const data  = await apiCall.json();
    console.log(data);
    if (city && country) {
      this.setState({
        temperature: data.main.temp,
        city: data.name,
        country: data.sys.country,
        description: data.weather[0].description,
        humidity: data.main.humidity,
        icon: data.weather[0].icon,
        error: ""
      });
    } else {
      this.setState({
        temperature: undefined,
        city: undefined,
        country: undefined,
        description: undefined,
        error: "Error: try again."
      });
    }
  }
  render() {
    return (
      <div>
      <div className="wrapper">
        <div className="main">
          <div className="container">
            <div className="row"></div>
              <div className="col-xs-6 title-container">
                <Titles />
              </div>
              <div className="col-xs-6 form-container">
                <Form getWeather={this.getWeather}/>
                <Weather
                  temperature={this.state.temperature}
                  city={this.state.city}
                  country={this.state.country}
                  description={this.state.description}
                  humidity={this.state.humidity}
                  icon={this.state.icon}
                  error={this.state.error}
                  />
              </div>
              <div id="icon">
                <img id="wicon" src="" alt="Weather icon"></img>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
};

export default App;
